function generateGrid(){var t=Math.abs(Alpine.store("settings").base),s=Math.abs(Alpine.store("settings").grid_base),n=Math.abs(Alpine.store("settings").iterations);let i=[];for(let e=1;e<=n;e++){var r=s*e;i.push({p:r+"px",e:r/t+"em",r:r/t+"rem"})}Alpine.store("settings").grids=i}document.addEventListener("DOMContentLoaded",()=>{});